close all;fig2 = figure;Oxy21 = [2.5 3 4];Oxy16 = [2 3 4];Oxy11 = [3 3 2.5];y = [Oxy21; Oxy16; Oxy11];x = categorical({'Oxy21%','Oxy16%','Oxy11%'});x = reordercats(x,{'Oxy21%','Oxy16%','Oxy11%'});%ylim([0 5]);set(gca,'fontname','Carial','FontSize',20); % Set it to timesset(gca,'LineWidth',2);set(gca,'fontweight','bold');bp = bar(x,y,'BarWidth',1);%hold onxlabel('Oxy Concentration','FontSize',20);ylabel('Combustion duration (ms)','FontSize',20);bp(1).FaceColor = 'r';bp(2).FaceColor = 'g';bp(3).FaceColor = 'b';legend('A','B','C','FontSize',14);%%%{y = [10,20,30,15];a = bar(y);labels = {'A', 'B', 'C', 'D'};xt = get(gca, 'XTick');text(xt, y, labels, 'HorizontalAlignment','center', 'VerticalAlignment','bottom')%}%%