close all;fig2 = figure;Oxy21 = [2.5 3 4];Oxy16 = [2 3 4];Oxy11 = [3 3 2.5];y = [Oxy21; Oxy16; Oxy11];%err = 0.3*y;%y = [Oxy21 Oxy16 Oxy11];%x = categorical({'Oxy21%','Oxy16%','Oxy11%'});%x = reordercats(x,{'Oxy21%','Oxy16%','Oxy11%'});%ylim([0 5]);%bp = bar(x,y,'BarWidth',1); %Acceptable range [0 1] bp = bar(y,'BarWidth',1,'LineWidth',1.5); %Acceptable range [0 1] hold on%title('What is my name?','FontSize',20,'Position',[2 4.7])xlabel('Oxy Concentration','FontSize',20);ylabel('Combustion duration (ms)','FontSize',20);%{hp = bar(y);cm = colororder; % or replace with the desired colormaphatchfill2(hp(1),'single','HatchAngle',0,'hatchcolor',cm(1,:));hatchfill2(hp(2),'cross','HatchAngle',45,'hatchcolor',cm(2,:));hatchfill2(hp(3),'single','HatchAngle',45,'hatchcolor',cm(3,:));for i = 1:numel(hp)    hp(i).FaceColor = 'none';end%}%patch(bp(1));cm = colororder; % or replace with the desired colormapA = hatchfill2(bp(1),'single','HatchAngle',0,'hatchcolor',[1 0 0],'HatchLineWidth',1.5);B = hatchfill2(bp(2),'cross','HatchAngle',45,'hatchcolor',[0 1 0],'HatchLineWidth',1.5);C = hatchfill2(bp(3),'single','HatchAngle',45,'hatchcolor',[0 0 1],'HatchLineWidth',1.5);for i = 1:numel(bp)    bp(i).FaceColor = 'none';endhold on%bp(1).FaceColor = 'r';%bp(2).FaceColor = 'g';%bp(3).FaceColor = 'b';legend([A B C],{'A' 'B' 'C'},'FontSize',20);%set(gca,'YTick',0:1:4,'YTickLabel',0:1:5)set(gca,'YLim',[0 5]);set(gca,'YTick',0:1:5);set(gca, 'XTickLabel', {'Oxy21%','Oxy16%','Oxy11%'});ax = gca;ax.XAxis.FontSize = 18; %Xtick fontsize %https://www.mathworks.com/matlabcentral/answers/511665-fontsize-and-properties-of-xticklabels-using-figure-handlesax.YAxis.FontSize = 18; %Ytick fontsize%text(,num2str(y(:,1)),'HorizontalAlignment','center','VerticalAlignment','bottom');%errhigh = 0.1*y;%errlow  = (-0.1)*y;%er = errorbar(x,y,errlow,errhigh);   set(gca,'fontname','Carial','FontSize',20); % Set it to timesset(gca,'LineWidth',1.5);set(gca,'fontweight','bold');grid onhold on%%%{y = [10,20,30,15];a = bar(y);labels = {'A', 'B', 'C', 'D'};xt = get(gca, 'XTick');text(xt, y, labels, 'HorizontalAlignment','center', 'VerticalAlignment','bottom')%}%%